function burgerMenu(){const e=document.querySelector(".burger"),s=document.querySelector(".menu"),t=document.querySelector("body"),c=document.querySelector(".nav__btn");e.addEventListener("click",(()=>{s.classList.contains("active")?(s.classList.remove("active"),e.classList.remove("active-burger"),t.classList.remove("locked"),c.classList.remove("active")):(s.classList.add("active"),e.classList.add("active-burger"),t.classList.add("locked"),c.classList.add("active"))})),window.addEventListener("resize",(()=>{window.innerWidth>991.98&&(s.classList.remove("active"),e.classList.remove("active-burger"),t.classList.remove("locked"),c.classList.remove("active"))}))}burgerMenu();